import React from 'react';
import './Home.css';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

class Home extends React.Component {
    constructor() {
        super();
        
        this.state = {
            SisaUang : 0,
            persentaseUang : 0,
            pemasukkanUang : 0,
            pengeluaranuang : 0,
            transaksiIN : 0,
            transaksiOUT : 0,
            summary : [
                {
                    deskripsi : 'Uang Bulanan',
                    tanggal :'3 Mei 2023',
                    nominal : 10000,
                    category : 'IN'
                },
            
                {
                    deskripsi : 'Skincare',
                    tanggal :'5 Mei 2023',
                    nominal : 10000,
                    category : 'OUT'
                }
            ]
            
        }

        this.tambahItem = this.tambahItem.bind(this);
        this.fnHitung = this.fnHitung.bind(this);
        
    }

    tambahItem(objek) {
        let newData = [...this.state.summary, objek]
        let dataUangIN = newData.filter( (item)=> item.category === 'IN');
        let nominalUang = dataUangIN.map((item)=> item.nominal);
        let jumlahUangIn = nominalUang.reduce((total, num)=> total + num );

        let dataUangOUT = newData.filter( (item)=> item.category === 'OUT');
        let nominalUangOUT = dataUangOUT.map((item)=> item.nominal);
        let jumlahUangOUT = nominalUangOUT.reduce((total, num)=> total + num )

            this.setState({
        pemasukkanUang : jumlahUangIn,
        transaksiIN :nominalUang.length,
        pengeluaranuang : jumlahUangOUT,
        transaksiOUT : nominalUangOUT.length,
        SisaUang : jumlahUangIn - jumlahUangOUT,
        persentaseUang : (jumlahUangIn - jumlahUangOUT)/jumlahUangIn * 100,
        summary : newData

       })

    }

    fnHitung(){
        let dataUangIN = this.state.summary.filter( (item)=> item.category === 'IN');
        let nominalUang = dataUangIN.map((item)=> item.nominal);
        let jumlahUangIn = nominalUang.reduce((total, num)=> total + num );

        let dataUangOUT = this.state.summary.filter( (item)=> item.category === 'OUT');
        let nominalUangOUT = dataUangOUT.map((item)=> item.nominal);
        let jumlahUangOUT = nominalUangOUT.reduce((total, num)=> total + num )
        

       this.setState({
        pemasukkanUang : jumlahUangIn,
        transaksiIN :nominalUang.length,
        pengeluaranuang : jumlahUangOUT,
        transaksiOUT : nominalUangOUT.length,
        SisaUang : jumlahUangIn - jumlahUangOUT,
        persentaseUang : (jumlahUangIn - jumlahUangOUT)/jumlahUangIn * 100

       })
    }
     
    componentDidMount() {
        this.fnHitung()
    }

    render() {
        return (
            <>
                <div className='container py-5'>
                    <div className='row'>
                        <div className='col-12 text-center'>
                            <h1 className='fw-bold'> MUCHEN APPS </h1>
                            <hr className='w-75 mx-auto'/>
                            <h2 className='fw-bold'>Rp.{this.state.SisaUang},-</h2>
                            <span className='title-md'>Uang kamu tersisa {this.state.persentaseUang}% lagi</span>
                        </div>
                    </div>

                    <div className='row mt-4'>
                        <div className='col-6'>
                            <div className='card-wrapper p-4'>
                                <div className='icon-wrapper mb-1'>
                                    <i className="bi bi-wallet2"></i>
                                </div>
                                <span className='title-sm '>Pemasukkan</span>
                                <h3 className='fw-bold'>Rp.{this.state.pemasukkanUang},-</h3>
                                <div>
                                    <span className='title-sm text-ungu fw-bold'>{this.state.transaksiIN}</span><span className='title-sm'> Transaksi</span>
                                </div>
                            </div>
                        </div>

                        <div className='col-6'>
                            <div className='card-wrapper p-4'>
                                <div className='icon-wrapper mb-1'>
                                <i className="bi bi-cash-coin"></i>
                                </div>
                                <span className='title-sm '>Pengeluaran</span>
                                <h3 className='fw-bold'>Rp.{this.state.pengeluaranuang},-</h3>
                                <div>
                                    <span className='title-sm text-ungu fw-bold'>{this.state.transaksiOUT}</span><span className='title-sm'> Transaksi</span>
                                </div>
                            </div>
                        </div>

                    </div>

                    <div className='row mt-5'>
                        <div className='col-12 d-flex justify-content-between align-items-center'>
                            <h4 className='ringkasan'> Ringkasan Transaksi </h4>
                            <div className='wrapper-button d-flex'>
                                <ModalCreate action={this.tambahItem} category="IN" variant="button btn-ungu px-3 py-2 me-1" text="Pemasukkan" icon="bi bi-patch-plus-fill" modalheading="Tambahkan Pemasukkan"/>
                                <ModalCreate action={this.tambahItem} category="OUT" variant="button btn-pink px-3 py-2" text="Pengeluaran" icon="bi bi-patch-minus-fill" modalheading="Tambahkan Pengeluaran"/>
                            </div> 
                        </div>
                    </div>

                    <div className='row mt-4'>
                        { this.state.summary.map((sum, index)=> {
                            return (
                                <div key={index}className='mb-3 col-12 d-flex justify-content-between align-items-center'>
                                <div className='d-flex align-items-center'>
                                <div className={sum.category === 'IN' ? 'icon-wrapper-IN' : 'icon-wrapper-OUT'}>
                                <i className={sum.category === 'IN' ? 'bi bi-bag-plus-fill' : '<bi bi-bag-dash-fill'}></i>
                                </div>
                                <div className='transaction ms-3 d-flex flex-column'>
                                    <h6>{sum.deskripsi}</h6>
                                    <span className='title-sm'>{sum.tanggal}</span>
    
                                </div>
                                </div>
                                <h5 className={sum.category === 'IN' ? 'text-money-in' : 'text-money-out'}>Rp.{sum.nominal} </h5>
                            </div>
                            )
                        })}
                    
                    </div>
                </div>
            </>
        )
    }
}


class ModalCreate extends React.Component {
    constructor() {
        super();
        this.state = {
            show : false,
            deskripsi : '',
            nominal : 0,
            tanggal : '',
            category : ''
        }

        this.handleClose = this.handleClose.bind(this);
        this.handleShow = this.handleShow.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.tambahItem = this.tambahItem.bind(this);
    }

    handleClose() {
        this.setState({
            show : false
        })
    }

    handleShow() {
        this.setState({
            show : true,
            category : this.props.category
        })
    }

    handleChange(evt) {  
            this.setState({
                [evt.target.name] : evt.target.value
            })

    }

    tambahItem() {
        const Data = {
            deskripsi : this.state.deskripsi,
            nominal : parseInt(this.state.nominal),
            tanggal : this.state.tanggal,
            category : this.state.category
        }
        const fnTambahItem = this.props.action;
        fnTambahItem(Data);
        this.setState({
            show : false
        })

    }

    render() {
        return (
            <>
       <button onClick={this.handleShow} className={this.props.variant}>{this.props.text} <i className={this.props.icon}></i> </button>
  
        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>{this.props.modalheading}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <div className="mb-3">
  <label className="form-label">Deskripsi</label>
  <input 
  type="text" 
  className="form-control"
   placeholder="Masukkan Deskripsi"
    name='deskripsi'
     value={this.state.deskripsi} 
     onChange={this.handleChange}
     />
</div>

<div className="mb-3">
  <label className="form-label">Nominal</label>
  <input 
  type="number" 
  className="form-control"
   placeholder="Masukkan Deskripsi"
    name='nominal'
     value={this.state.nominal} 
     onChange={this.handleChange}
     />
</div>

<div className="mb-3">
  <label className="form-label">Tanggal</label>
  <input 
  type="date" 
  className="form-control"
   placeholder="Masukkan Deskripsi"
    name='tanggal'
     value={this.state.tanggal} 
     onChange={this.handleChange}
     />
</div>

<div>
  <input 
  type="hidden" 
  className="form-control"
   placeholder="masukkan deskripsi"
    name='category'
     value={this.state.category} 
     onChange={this.handleChange}
     />
</div>

          </Modal.Body>
          <Modal.Footer>
            <button className={this.props.variant} onClick={this.tambahItem}>
              Save  
            </button>
          </Modal.Footer>
        </Modal>
      </>
        )
    }
}  

export default Home;
